#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="C:\Users\Leo\Documents\Code\ML_onChain\node_modules\.pnpm\csv-parser@3.0.0\node_modules\csv-parser\bin\node_modules;C:\Users\Leo\Documents\Code\ML_onChain\node_modules\.pnpm\csv-parser@3.0.0\node_modules\csv-parser\node_modules;C:\Users\Leo\Documents\Code\ML_onChain\node_modules\.pnpm\csv-parser@3.0.0\node_modules;C:\Users\Leo\Documents\Code\ML_onChain\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/c/Users/Leo/Documents/Code/ML_onChain/node_modules/.pnpm/csv-parser@3.0.0/node_modules/csv-parser/bin/node_modules:/mnt/c/Users/Leo/Documents/Code/ML_onChain/node_modules/.pnpm/csv-parser@3.0.0/node_modules/csv-parser/node_modules:/mnt/c/Users/Leo/Documents/Code/ML_onChain/node_modules/.pnpm/csv-parser@3.0.0/node_modules:/mnt/c/Users/Leo/Documents/Code/ML_onChain/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../csv-parser/bin/csv-parser" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../csv-parser/bin/csv-parser" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../csv-parser/bin/csv-parser" $args
  } else {
    & "node$exe"  "$basedir/../csv-parser/bin/csv-parser" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
